CREATE TABLE Users (
   name VARCHAR(32) NOT NULL,
   pass VARCHAR(32) NOT NULL,
   salt VARCHAR(4)  NOT NULL,
   PRIMARY KEY (name)
)
ENGINE=INNODB;


CREATE TABLE GameTypes (
   gametype   VARCHAR(16),
   PRIMARY KEY (gametype)
)
ENGINE=INNODB;

INSERT INTO GameTypes
VALUES ('arithmetic'), ('synonyms'), ('antonyms'), ('translation');


CREATE TABLE Rankings (
   gametype   VARCHAR(16) NOT NULL,
   boardsize  TINYINT NOT NULL,
   name       VARCHAR(32) NOT NULL,
   score      INT NOT NULL,
   timestamp  BIGINT NOT NULL,
   INDEX (score, timestamp),
   FOREIGN KEY (name) REFERENCES Users (name) ON DELETE CASCADE,
   FOREIGN KEY (gametype) REFERENCES GameTypes (gametype)
)
ENGINE=INNODB;


CREATE TABLE Synonyms (
   id         INTEGER UNSIGNED AUTO_INCREMENT,
   question   VARCHAR(32) NOT NULL,
   answer     VARCHAR(32) NOT NULL,
   PRIMARY KEY (id)
)
ENGINE=INNODB;


CREATE TABLE Antonyms (
   id         INTEGER UNSIGNED AUTO_INCREMENT,
   question   VARCHAR(32) NOT NULL,
   answer     VARCHAR(32) NOT NULL,
   PRIMARY KEY (id)
)
ENGINE=INNODB;


CREATE TABLE Translation (
   id         INTEGER UNSIGNED AUTO_INCREMENT,
   question   VARCHAR(32) NOT NULL,
   answer     VARCHAR(32) NOT NULL,
   PRIMARY KEY (id)
)
ENGINE=INNODB;


